export function splitTypedError(errorStr: string): TypedError {
    const typed = errorStr.split('>>>').at(-1) as NapiCallError;

    if (!typed) {
        return {
            typed: 'unknown',
            extra: errorStr,
        };
    }

    const parts = typed.split(':::');
    if (parts.length === 2) {
        return {
            typed: parts[0] as NapiCallError,
            extra: parts[1],
            sub: '',
        };
    } else {
        return {
            typed: parts[0] as NapiCallError,
            extra: '',
            sub: parts[1] as BrowserExtErrors,
        };
    }
}

